import React, { useMemo } from "react";
import { Link, useParams } from "react-router-dom";
import { useListingPool } from "../data/listings";

const formatPrice = (n) =>
  new Intl.NumberFormat("tr-TR", { style: "currency", currency: "TRY", maximumFractionDigits: 0 }).format(n);
const formatDate = (iso) => new Date(iso).toLocaleDateString("tr-TR", { day: "2-digit", month: "2-digit", year: "numeric" });

export default function ListingDetail() {
  const { id } = useParams();
  const { pool } = useListingPool();
  const item = useMemo(() => pool.find((l) => l.id === id), [pool, id]);

  if (!item) {
    return (
      <div className="mx-auto max-w-5xl px-4 sm:px-6 lg:px-8 py-8">
        <div className="rounded-2xl border bg-white p-8 text-center">
          <h1 className="text-xl font-semibold">Aradığınız ilan yayında değil ya da kaldırıldı.</h1>
          <p className="mt-2 text-gray-600">Ana sayfaya dönerek güncel ilanlara göz atabilirsiniz.</p>
          <Link to="/" className="mt-4 inline-block rounded-2xl border px-4 py-2 hover:bg-gray-50">Ana sayfa</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="mx-auto max-w-5xl px-4 sm:px-6 lg:px-8 py-6">
      <nav className="text-sm text-gray-500">
        <Link to="/" className="hover:underline">Ana sayfa</Link><span className="mx-2">/</span><span>İlan Detayı</span>
      </nav>

      <div className="mt-4 grid grid-cols-1 gap-6 lg:grid-cols-3">
        <div className="lg:col-span-2 overflow-hidden rounded-2xl border bg-white">
          <div className="aspect-[16/10] w-full">
            <img src={item.image} alt={item.title} className="h-full w-full object-cover" />
          </div>
          <div className="p-5">
            <h1 className="text-2xl font-bold">{item.title}</h1>
            <div className="mt-1 text-gray-600">{item.location}</div>
            <div className="mt-1 text-xs text-gray-400">İlan tarihi: {formatDate(item.postedAt)}</div>
            <div className="mt-4 text-2xl font-extrabold">{formatPrice(item.price)}</div>
            <div className="mt-3 inline-flex items-center rounded-full border px-3 py-1 text-xs text-gray-600">
              Kategori: <span className="ml-1 font-semibold">{item.category}</span>
            </div>
            {item.description && <p className="mt-5 leading-relaxed text-gray-700">{item.description}</p>}
          </div>
        </div>

        <aside className="lg:col-span-1 space-y-4">
          <div className="rounded-2xl border bg-white p-5">
            <h2 className="text-lg font-semibold">Satıcı Bilgisi</h2>
            <div className="mt-2 text-sm text-gray-600">Doğrulanmış Satıcı (Demo)</div>
            <button className="mt-4 w-full rounded-2xl border px-4 py-2 font-medium hover:bg-gray-50">Mesaj Gönder</button>
            <button className="mt-2 w-full rounded-2xl bg-black px-4 py-2 font-medium text-white hover:opacity-90">Telefonu Göster</button>
          </div>

          <div className="rounded-2xl border bg-white p-5">
            <h3 className="text-base font-semibold">Güvenlik İpuçları</h3>
            <ul className="mt-2 list-disc pl-5 text-sm text-gray-600 space-y-1">
              <li>Görmeden ödeme yapmayın.</li>
              <li>Yüz yüze ve güvenli noktalarda buluşun.</li>
              <li>Şüpheli içerikleri moderasyona bildirin.</li>
            </ul>
          </div>
        </aside>
      </div>
    </div>
  );
}
